package br.com.pedrelosa.campoMinado.modelo;import br.com.pedrelosa.campoMinado.excecao.ExplosaoException;import java.util.ArrayList;import java.util.List;public class Campo {		private final int x;	private final int y;		private boolean minado;	private boolean aberto;	private boolean marcado;		private List<Campo> vizinhos = new ArrayList<>();		public Campo(int x, int y) {		this.x = x;		this.y = y;	}		public boolean adicionarVizinho(Campo vizinho) {				boolean xIsDif = this.x != vizinho.x;		boolean yIsDif = this.y != vizinho.y;		boolean diagonalIsDif = xIsDif && yIsDif;				int deltaX = Math.abs(this.x - vizinho.x);		int deltaY = Math.abs(this.y - vizinho.y);		int deltaAll = deltaX + deltaY;				if (deltaAll == 1 && ! diagonalIsDif) {			vizinhos.add(vizinho);			return true;		} else if (deltaAll == 2 && diagonalIsDif) {			vizinhos.add(vizinho);			return true;		} else {			return false;		}			}		public void alternarMarcacao(){		if (!this.aberto){			this.marcado = !this.marcado;					}	}		public boolean abrir(){		if (!this.aberto && !this.marcado){			this.aberto = true;						if (this.minado){				throw new ExplosaoException();			}						if (vizinhancaSegura()){				vizinhos.forEach(Campo::abrir);			}						return true;		}else {			return false;		}			}		public boolean vizinhancaSegura(){		return vizinhos.stream().noneMatch(v -> v.minado);	}		public boolean isMarcado(){		return this.marcado;	}	}